const hamburger=document.getElementById("hamburger-btn"),navMenu=document.getElementById("nav-menu");function loadHeader(){fetch("assets/components/header.html").then(e=>e.text()).then(e=>{const t=document.getElementById("header-placeholder");t&&(t.innerHTML=e,highlightActiveLink())}).catch(e=>{console.error("Error loading header:",e)})}function highlightActiveLink(){const e=window.location.pathname.split("/").pop();document.querySelectorAll(".nav-link").forEach(t=>{t.getAttribute("href")===e&&t.classList.add("nav-active")})}hamburger.addEventListener("click",()=>{navMenu.classList.toggle("show")}),document.addEventListener("DOMContentLoaded",()=>{loadHeader()});
