function loadHeader(){fetch("assets/components/header.html").then(e=>e.text()).then(e=>{const t=document.getElementById("header-placeholder");t&&(t.innerHTML=e,highlightActiveLink(),initHamburger())}).catch(e=>{console.error("Error loading header:",e)})}function highlightActiveLink(){const e=window.location.pathname.split("/").pop();document.querySelectorAll(".nav-link").forEach(t=>{t.getAttribute("href")===e&&t.classList.add("nav-active")})}function initHamburger(){const e=document.getElementById("hamburger-btn"),t=document.getElementById("nav-menu");e&&t&&e.addEventListener("click",()=>{t.classList.toggle("show")})}document.addEventListener("DOMContentLoaded",()=>{loadHeader()});
